import React, { useState } from 'react';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import PerfectScrollbar from 'react-perfect-scrollbar';
import {
  Button,
  Box,
  Card,
  Chip,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  FormControl,
  FormGroup,
  Grid,
  InputLabel,
  Select,
  MenuItem,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TablePagination,
  TableRow,
  TextField,
  makeStyles,
} from '@material-ui/core';
import OpenInBrowserIcon from '@material-ui/icons/OpenInBrowser';

import axios from 'axios';

const token = localStorage.jwt;

const useStyles = makeStyles((theme) => ({
  root: {},
  avatar: {
    marginRight: theme.spacing(2)
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  }
}));

const Donors = ({ className, bloodDonors, ...rest }) => {
  const classes = useStyles();
  const [selectedCustomerIds, setSelectedCustomerIds] = useState([]);
  const [limit, setLimit] = useState(10);
  const [page, setPage] = useState(0);
  const [open, setOpen] = React.useState(false);
  const [state, setState] = React.useState('');


  const resetSelectedIDs = () => {
    setSelectedCustomerIds([]);
  }

  const handleChange = (event) => {
    setState(event.target.value);
  };

  const handleClickOpen = (event, id) => {
    handleSelectOne(event,id);
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    resetSelectedIDs();
  };

  const handleSubmit = (props) => {
    let formData =  new FormData(document.getElementById("updateMultiple"));
    var object = {};
    formData.forEach(function(value, key){
        object[key] = value;
    });
    console.log(object);

        for(let i in selectedCustomerIds){
          console.log(`${i}: `,selectedCustomerIds);
          axios({
            method: 'PUT',
            url: `http://localhost:1337/blood-donors/${selectedCustomerIds[i]}`,
            headers: {
              Authorization: `Bearer ${token}`
            },
            data: object
          })
          //handle success
          .then((response) => {
            const donor = response.data;
            console.log(donor);
            handleClose();
            window.location.reload();      
          })
          //handle error
          .catch(error => console.error(`Error: ${error}`));
        }
  };

  const handleSelectOne = (event, id) => {
    const selectedIndex = selectedCustomerIds.indexOf(id);
    let newSelectedCustomerIds = [];

    if (selectedIndex === -1) {
      newSelectedCustomerIds = newSelectedCustomerIds.concat(selectedCustomerIds, id);
    } else if (selectedIndex === 0) {
      newSelectedCustomerIds = newSelectedCustomerIds.concat(selectedCustomerIds.slice(1));
    } else if (selectedIndex === selectedCustomerIds.length - 1) {
      newSelectedCustomerIds = newSelectedCustomerIds.concat(selectedCustomerIds.slice(0, -1));
    } else if (selectedIndex > 0) {
      newSelectedCustomerIds = newSelectedCustomerIds.concat(
        selectedCustomerIds.slice(0, selectedIndex),
        selectedCustomerIds.slice(selectedIndex + 1)
      );
    }

    setSelectedCustomerIds(newSelectedCustomerIds);
    console.log('Selected IDs: ', newSelectedCustomerIds);
  };

  const handleLimitChange = (event) => {
    setLimit(event.target.value);
  };

  const handlePageChange = (event, newPage) => {
    setPage(newPage);
  };

  const displayTable = (bloodDonors) => {
    const { firstName,lastName, bloodGroup, email, phone, sex, previousPregnancies, previousTransfusions, previousReactions } = state

    return (
      <>
        <Box my={3} mx={3}>
        <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
              <form encType="multipart/form-data" onSubmit={handleSubmit}  method="post" id="updateMultiple">
                <FormGroup>
                  <DialogTitle id="form-dialog-title">Update A Donor</DialogTitle>
                  <DialogContent>
                    <DialogContentText>
                      To update a Donor, please enter the Donor information here.
                    </DialogContentText>
                    <FormControl required className={classes.formControl} id="donorForm">
                      <Grid container spacing={2}>
                        <Grid item xs={6}>
                          <TextField
                            id="firstName"
                            label="NEW First Name"
                            name="firstName"
                            type="text"
                            value={firstName}
                            onChange={handleChange}
                            className={classes.textField}
                            InputLabelProps={{
                              shrink: true,
                            }}
                          />
                        </Grid>
                        <Grid item xs={6}>
                          <TextField
                            id="lastName"
                            label="NEW Last Name"
                            name="lastName"
                            type="text"
                            value={lastName}
                            onChange={handleChange}
                            className={classes.textField}
                            InputLabelProps={{
                              shrink: true,
                            }}
                          />
                        </Grid>
                        <Grid item xs={6}>
                          <FormControl required style={{ width: '100%'}}>
                            <InputLabel shrink id="bloodGroup">
                            NEW Blood Group
                            </InputLabel>
                            <Select
                                lableId="bloodGroup"
                                id="bloodGroup"
                                label="NEW Blood Group"
                                name="bloodGroup"
                                type="text"
                                value={bloodGroup}
                                onChange={handleChange}
                                InputLabelProps={{
                                  shrink: false,
                                }}
                                style={{ width: '75%'}}
                              >
                              <MenuItem value={'A-'}>A-</MenuItem>
                              <MenuItem value={'A+'}>A+</MenuItem>
                              <MenuItem value={'B-'}>B-</MenuItem>
                              <MenuItem value={'B+'}>B+</MenuItem>
                              <MenuItem value={'AB-'}>AB-</MenuItem>
                              <MenuItem value={'AB+'}>AB+</MenuItem>
                              <MenuItem value={'O-'}>O-</MenuItem>
                              <MenuItem value={'O+'}>O+</MenuItem>
                            </Select>
                          </FormControl>
                        </Grid>
                        <Grid item xs={6}>
                          <FormControl required style={{ width: '100%'}}>
                            <InputLabel shrink id="sex">
                            Sex
                            </InputLabel>
                            <Select
                                lableId="sex"
                                id="sex"
                                label="Sex"
                                name="sex"
                                type="text"
                                value={sex}
                                onChange={handleChange}
                                InputLabelProps={{
                                  shrink: false,
                                }}
                                style={{ width: '75%'}}
                              >
                              <MenuItem value="male">Male</MenuItem>
                              <MenuItem value="female">Female</MenuItem>
                            </Select>
                          </FormControl>
                        </Grid>
                        <Grid item xs={6}>
                          <TextField
                            id="email"
                            label="NEW Email"
                            name="email"
                            type="text"
                            value={email}
                            onChange={handleChange}
                            className={classes.textField}
                            InputLabelProps={{
                              shrink: true,
                            }}
                          />
                        </Grid>
                          <Grid item xs={6}>
                          <TextField
                            id="phone"
                            label="NEW Phone"
                            name="phone"
                            type="text"
                            value={phone}
                            onChange={handleChange}
                            className={classes.textField}
                            InputLabelProps={{
                              shrink: true,
                            }}
                          />
                        </Grid>
                        <Grid item xs={6}>
                          <FormControl required style={{ width: '100%'}}>
                            <InputLabel shrink id="previousPregnancies">
                            Previous Pregnancies
                            </InputLabel>
                            <Select
                                lableId="previousPregnancies"
                                id="previousPregnancies"
                                label="Previous Pregnancies"
                                name="previousPregnancies"
                                type="text"
                                defaultValue="no"
                                value={previousPregnancies}
                                onChange={handleChange}
                                InputLabelProps={{
                                  shrink: false,
                                }}
                                style={{ width: '75%'}}
                              >
                              <MenuItem value={'yes'}>Yes</MenuItem>
                              <MenuItem value={'no'}>No</MenuItem>
                            </Select>
                          </FormControl>
                        </Grid>
                        <Grid item xs={6}>
                          <FormControl required style={{ width: '100%'}}>
                            <InputLabel shrink id="previousTransfusions">
                            Previous Transfusions
                            </InputLabel>
                            <Select
                                lableId="previousTransfusions"
                                id="previousTransfusions"
                                label="Previous Transfusionss"
                                name="previousTransfusions"
                                type="text"
                                value={previousTransfusions}
                                onChange={handleChange}
                                InputLabelProps={{
                                  shrink: false,
                                }}
                                style={{ width: '75%'}}
                              >
                              <MenuItem value={'yes'}>Yes</MenuItem>
                              <MenuItem value={'no'}>No</MenuItem>
                            </Select>
                          </FormControl>
                        </Grid>
                        <Grid item xs={6}>
                          <FormControl required style={{ width: '100%'}}>
                            <InputLabel shrink id="previousReactions">
                            Previous Reactions
                            </InputLabel>
                            <Select
                                lableId="previousReactions"
                                id="previousReactions"
                                label="Previous Reactions"
                                name="previousReactions"
                                type="text"
                                value={previousReactions}
                                onChange={handleChange}
                                InputLabelProps={{
                                  shrink: false,
                                }}
                                style={{ width: '75%'}}
                              >
                              <MenuItem value={'yes'}>Yes</MenuItem>
                              <MenuItem value={'no'}>No</MenuItem>
                            </Select>
                          </FormControl>
                        </Grid>
                      </Grid>
                    </FormControl>
                  </DialogContent>
                  <DialogActions>
                    <Button onClick={handleClose} color="primary" form="updateMultipleForm">
                      Cancel
                    </Button>
                    <Button onClick={handleSubmit} color="primary" type="submit" form="updateMultipleForm">
                      Submit
                    </Button>
                  </DialogActions>
                </FormGroup>
              </form>
            </Dialog>
        </Box>
      <Table>
      <TableHead>
          <TableRow>
            <TableCell>
            Action
            </TableCell>
            <TableCell>
            Donor ID
            </TableCell>
            <TableCell>
            Name
            </TableCell>
            <TableCell>
            Email Address
            </TableCell>
            <TableCell>
            Blood Group
            </TableCell>
            <TableCell>
            Phone Number
            </TableCell>
            <TableCell>
            Previous Transfusions
            </TableCell>
            <TableCell>
            Previous Reactions
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
        {bloodDonors
        .slice(page * limit, page * limit + limit)
        .map((bloodDonor) => (
          <>
            <TableRow
              hover
              key={bloodDonor.id}
              selected={selectedCustomerIds.indexOf(bloodDonor.id) !== -1}
            >
              <TableCell>
                <Chip icon={<OpenInBrowserIcon />} label="Update" color="secondary" onClick={(event) => {handleClickOpen(event,bloodDonor.id)}}/>
              </TableCell>
              <TableCell>
                {bloodDonor.id.slice(17)}
              </TableCell>
              <TableCell>
                {bloodDonor.firstName + " " + bloodDonor.lastName}
              </TableCell>
              <TableCell>
                {bloodDonor.email}
              </TableCell>
              <TableCell>
                {bloodDonor.bloodGroup}
              </TableCell>
              <TableCell>
                {bloodDonor.phone}
              </TableCell>
              <TableCell>
                {bloodDonor.previousTransfusions}
              </TableCell>
              <TableCell>
                {bloodDonor.previousReactions}
              </TableCell>
            </TableRow>
            </>
          ))}
        </TableBody>
      </Table>
      </>
    )
  }

  return (
    <Card
      className={clsx(classes.root, className)}
      {...rest}
    >
      <PerfectScrollbar>
        <Box minWidth={1050}>
          {displayTable(bloodDonors)}
        </Box>
      </PerfectScrollbar>
      <TablePagination
        component="div"
        count={bloodDonors.length}
        onChangePage={handlePageChange}
        onChangeRowsPerPage={handleLimitChange}
        page={page}
        rowsPerPage={limit}
        rowsPerPageOptions={[5, 10, 25, 50, 100]}
      />
    </Card>
  );
};

Donors.propTypes = {
  className: PropTypes.string,
};

export default Donors;
